import React, { Component } from 'react';
import Card from 'react-bootstrap/esm/Card';
import Col from 'react-bootstrap/esm/Col';
import Form from 'react-bootstrap/Form';

class RecipeForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dishName: '',
      ingredients: '',
      steps: '',
    };
  }

  handleDishNameChange = (event) => {
    this.setState({ dishName: event.target.value });
  };

  handleIngredientsChange = (event) => {
    this.setState({ ingredients: event.target.value });
  };

  handleStepsChange = (event) => {
    this.setState({ steps: event.target.value });
  };

  render() {
    return (
      <>
        <div className='container p-5 justify-content-center'>
          <div className='row p-3 bg-primary'>
            <div className='col'>
              <div className='row' >
                  <Card bg={'light'} as = {Col} className='me-3' style={{height: '18,5em'}}></Card>
                  <div className='col'>
                    <div className='row'>
                      <Form as={Col}>
                      <Form.Group controlId="FormDishName" >
                      <Form.Label>Dish Name</Form.Label>
                      <Form.Control type="DishName" placeholder="Enter dish name"style={{width: '16em'}}/>
                      </Form.Group>
                      </Form>

                      <Form as={Col}>
                      <Form.Group controlId="Form" >
                      <Form.Label>Email address</Form.Label>
                      <Form.Control type="email" placeholder="Enter email"style={{width: '16em'}}/>
                      </Form.Group>
                      </Form>
                    </div>
                    <div className='row'>
                      <Form as={Col}>
                        <Form.Group controlId="FormIngredient" >
                        <Form.Label>Ingredients</Form.Label>
                        <Form.Control type="Ingredients" placeholder="Enter ingredients"style={{height: '12em'}}/>
                        </Form.Group>
                      </Form>
                    </div>
                  </div> 
                   <Form className='me-3'>
                <Form.Group controlId="FormStep" >
                <Form.Label>How To Do</Form.Label>
                <Form.Control type="Step" placeholder="Step Here"style={{height: '12em'}}/>
                </Form.Group>
              </Form>      
                </div>
              </div>
                  
          </div>
        </div>
      </>
      /*<div className="recipe-container">
        <div className="center-square">
          <img src="your_image_url" alt="Dish" />
          <div className="dish-name">
            <input
              type="text"
              placeholder="Enter Dish Name"
              value={this.state.dishName}
              onChange={this.handleDishNameChange}
            />
          </div>
          <div className="ingredients">
            <input
              type="text"
              placeholder="Enter Ingredients"
              value={this.state.ingredients}
              onChange={this.handleIngredientsChange}
            />
          </div>
          <div className="steps">
            <input
              type="text"
              placeholder="Enter Steps"
              value={this.state.steps}
              onChange={this.handleStepsChange}
            />
          </div>
        </div>
      </div>*/
    );
  }
}

export default RecipeForm;
